def solution(stdin: str) -> str:
    n, x, y = map(int, stdin.split('\n')[0].split())
    number = stdin.split('\n')[1]

    def get_remainder(number: str, x: int, y: int) -> int:
        return int(number[-x:]) % (10 ** y)

    def update_number(number: str, index: int) -> str:
        number = list(number)
        number[index] = '1' if number[index] == '0' else '0'
        return ''.join(number)

    def binary_search(number: str, x: int, y: int, target: int) -> int:
        left, right = 0, n
        while left < right:
            mid = (left + right) // 2
            if get_remainder(number, x, y) == target:
                right = mid
            else:
                left = mid + 1
        return left

    operations = 0
    for i in range(1, n):
        remainder = get_remainder(number, x, y)
        target = (remainder * pow(10, n - i, 10 ** x)) % (10 ** x)
        if target != 10 ** y:
            index = binary_search(number, x, y, target)
            number = update_number(number, index)
            operations += 1

    return str(operations)

