    def find_operations(a, b, c):
        ops = 0
        while a != b:
            if a < b:
                ops += b - a
                b = a
            else:
                ops += a - b
                a = b
        while b != c:
            if b < c:
                ops += c - b
                c = b
            else:
                ops += b - c
                b = c
        return ops

