def solution(stdin: str) -> str:
    n = int(stdin)

    def is_divisible_by_25(num: int) -> bool:
        return num % 25 == 0

    def is_leading_zero(num: int) -> bool:
        return num // 10 == 0 and num % 10 == 0

    def has_zero(num: int) -> bool:
        return '0' in str(num)

    def get_next_numbers(num: int) -> list:
        digits = [int(d) for d in str(num)]
        next_numbers = []

        for i in range(len(digits) - 1):
            digits[i], digits[i + 1] = digits[i + 1], digits[i]
            next_number = int(''.join(map(str, digits)))
            if not is_leading_zero(next_number) and not has_zero(next_number):
                next_numbers.append(next_number)
            digits[i], digits[i + 1] = digits[i + 1], digits[i]

        return next_numbers

    def find_min_moves(num: int) -> int:
        if is_divisible_by_25(num):
            return 0

        visited = set()
        queue = [(num, 0)]
        visited.add(num)

        while queue:
            curr, moves = queue.pop(0)

            if is_divisible_by_25(curr):
                return moves

            next_numbers = get_next_numbers(curr)

            for next_number in next_numbers:
                if next_number not in visited:
                    visited.add(next_number)
                    queue.append((next_number, moves + 1))

        return -1

    return str(find_min_moves(n))

