    def is_valid_option(option, graph):
        visited = [False] * (n + 1)
        visited[1] = True
        stack = [1]

        while stack:
            curr = stack.pop()
            for i, is_connected in enumerate(graph[curr], start=1):
                if is_connected == '1' and not visited[i]:
                    if option[i - 1] == '1':
                        visited[i] = True
                        stack.append(i)

        return all(visited[i] for i in range(1, n + 1))

    def generate_options(graph, k, options=[]):
        if len(options) == k:
            return options

        if not options:
            options.append(["0"] * len(graph))

        for i, option in enumerate(options):
            for j, is_connected in enumerate(graph[i + 1], start=1):
                if is_connected == '1' and option[j - 1] == '0':
                    new_option = option.copy()
                    new_option[j - 1] = '1'
                    if is_valid_option(new_option, graph):
                        options.append(new_option)

        return generate_options(graph, k, options)

    def build_graph(n, roads):
        graph = [['0'] * n for _ in range(n + 1)]

        for a, b in roads:
            graph[a][b - 1] = '1'
            graph[b][a - 1] = '1'

