    queries = []
    for i in range(1, q + 1):
        n, a, b = map(int, stdin.split('\n')[i].split())
        queries.append((n, a, b))

    def min_money(n: int, a: int, b: int) -> int:
        if a == b:
            return a * n
        if a < b:
            a, b = b, a
        if n <= a:
            return n * a
        else:
            k = n // (a + b)
            ans = k * (a + b) * a + min(n - k * (a + b), a) * a
            return ans

    result = []
    for query in queries:
        result.append(min_money(*query))

    return '\n'.join(map(str, result))

