        a, b = map(int, lines[i].split())
        adj_list[a].append(b)
        adj_list[b].append(a)

    visited = [False] * (N + 1)

    def dfs(node: int, path_length: int) -> int:
        if path_length == N:
            return 1

        visited[node] = True
        count = 0
        for neighbor in adj_list[node]:
            if not visited[neighbor]:
                count += dfs(neighbor, path_length + 1)

        visited[node] = False
        return count

    return str(dfs(1, 1))

