def solution(stdin: str) -> str:
    n, m = map(int, stdin.split()[0:2])
    a = list(map(int, stdin.split()[2:n+2]))
    special_offers = []
    for i in range(m):
        x, y, w = map(int, stdin.split()[n+2+i*3:n+5+i*3])
        special_offers.append((x, y, w))

    def find(parent, i):
        if parent[i] == i:
            return i
        parent[i] = find(parent, parent[i])
        return parent[i]

    def union(parent, rank, x, y):
        xroot = find(parent, x)
        yroot = find(parent, y)

        if xroot == yroot:
            return False
        if rank[xroot] < rank[yroot]:
            parent[xroot] = yroot
        elif rank[xroot] > rank[yroot]:
            parent[yroot] = xroot
        else:
            parent[yroot] = xroot
            rank[xroot] += 1
        return True

    parent = [i for i in range(n)]
    rank = [0] * n
    edges = []

    for x, y, w in special_offers:
        edges.append((x-1, y-1, w))

    for i in range(n):
        for j in range(i+1, n):
            edges.append((i, j, a[i] + a[j]))

    edges.sort(key=lambda x: x[2])

    min_cost = 0
    for x, y, w in edges:
        if union(parent, rank, x, y):
            min_cost += w

    return str(min_cost)

