    mod = 10**9 + 7

    def factorial(x):
        res = 1
        for i in range(1, x + 1):
            res = (res * i) % mod
        return res

    def power(base, exponent):
        res = 1
        for i in range(exponent):
            res = (res * base) % mod
        return res

    def count_ways(n, m):
        return factorial(n + m) // (factorial(n) * factorial(m))

    def count_ways_helper(n, m):
