        if u_root == v_root:
            return True
        if trees[u_root] == k:
            return False
        if trees[v_root] == k:
            return False
        union(parent, u, v, parent)
        return path_exists(parent, u, v, parent, k)

    def process_query(parent, trees, u, k, vertices):
