        moved = False
        for i in range(4):
            for j in range(4):
                if board[i][j] == 0:
                    moved = True
                    for k in range(j + 1, 4):
                        if board[i][k] != 0:
                            board[i][j] = board[i][k]
                            board[i][k] = 0
                            break

        for i in range(4):
            for j in range(3):
                if board[i][j] == board[i][j + 1] and board[i][j] != 0:
                    board[i][j] *= 2
                    board[i][j + 1] = 0
                    moved = True

        for i in range(4):
            for j in range(3):
                if board[i][j] == 0 and board[i][j + 1] != 0:
                    board[i][j] = board[i][j + 1]
                    board[i][j + 1] = 0
                    moved = True

        return board, moved

    def move_up(board: list) -> tuple:
        board = transpose(board)
        board, moved = move_left(board)
        board = transpose(board)
        return board, moved

    def move_right(board: list) -> tuple:
        board = reverse(board)
        board, moved = move_left(board)
        board = reverse(board)
        return board, moved

    def move_down(board: list) -> tuple:
        board = transpose(board)
        board = reverse(board)
        board, moved = move_left(board)
        board = reverse(board)
        board = transpose(board)
        return board, moved

    def transpose(board: list) -> list:
        return [[board[j][i] for j in range(4)] for i in range(4)]

    def reverse(board: list) -> list:
        return [row[::-1] for row in board]

    def merge_tiles(board: list) -> list:
        for i in range(4):
            for j in range(3):
                if board[i][j] == board[i][j + 1] and board[i][j] != 0:
                    board[i][j] *= 2
                    board[i][j + 1] = 0

        return board

    def board_to_str(board: list) -> str:
        return '\n'.join([' '.join([str(cell) for cell in row]) for row in board])

