    def sing_songs(evening):
        songs = set()
        for villager in evening:
            if villager == 1:
                song = len(songs) + 1
                songs.add(song)
                yield song
            else:
                for song in songs:
                    yield song
                if villager in songs:
                    songs.remove(villager)

    def gather_villagers(evenings):
        all_songs = set()
        for evening in evenings:
            for song in sing_songs(evening):
                all_songs.add(song)
        return all_songs

    def known_songs(villagers, all_songs):
        known_songs = [set() for _ in villagers]
        for song in all_songs:
            for villager in villagers:
                if villager in song:
                    known_songs[villager - 1].add(song)
        return known_songs

    def find_villagers(known_songs, all_songs):
        return [i + 1 for i, s in enumerate(known_songs) if s == all_songs]

    input_data = [line.strip().split() for line in stdin.split('\n') if line.strip()]
    N, E = map(int, input_data[0])
    evenings = [[int(v) for v in evening[1:]] for evening in input_data[1:]]

    all_songs = gather_villagers(evenings)
    known_songs = known_songs(range(1, N + 1), all_songs)
    villagers = find_villagers(known_songs, all_songs)

    return '\n'.join(map(str, sorted(villagers)))

