                unlikeliness = get_unlikeliness(samples[i], samples[j])
                if unlikeliness < min_unlikeliness:
                    min_unlikeliness = unlikeliness
                    min_tree = [(i, j)]
                elif unlikeliness == min_unlikeliness:
                    min_tree.append((i, j))
        return min_unlikeliness, min_tree

