    def find_macro(s):
        for i in range(len(s) // 2, 1, -1):
            for j in range(len(s) - i * 2):
                if s[j:j + i] == s[j + i:j + 2 * i]:
                    return s[:j], s[j:j + i], s[j + i:]
        return None, None, None

    def encode(s):
        start, macro, end = find_macro(s)
        if not macro:
            return s
        return 'M' + encode(start) + encode(macro) + encode(end)

    encoded = encode(stdin)
    return len(encoded)

