def solution(stdin: str) -> str:
    n = int(stdin.split("\n", 1)[0])
    numbers = [int(i) for i in stdin.split("\n")[1:]]

    def find_max(numbers: list) -> int:
        if len(numbers) == 0:
            return 0
        if len(numbers) == 1:
            return numbers[0]
        if len(numbers) == 2:
            return max(numbers)

        max_sum = 0
        for i in range(1, len(numbers)):
            left_sum = sum([num ** 2 for num in numbers[:i]])
            right_sum = sum(numbers[i:])
            current_sum = left_sum * right_sum
            max_sum = max(max_sum, current_sum)

        return max_sum

    return str(find_max(numbers))

