def solution(stdin: str) -> str:
    def is_valid(x, y, M, N):
        return 0 <= x < M and 0 <= y < N

    def bfs(M, N, coins):
        visited = [[False] * N for _ in range(M)]
        visited[0][0] = True
        queue = [(0, 0)]

        while queue:
            x, y = queue.pop(0)
            for dx, dy in [(-1, 0), (0, -1), (1, 0), (0, 1)]:
                nx, ny = x + dx, y + dy
                if is_valid(nx, ny, M, N) and not visited[nx][ny]:
                    visited[nx][ny] = True
                    queue.append((nx, ny))

        return visited[M - 1][N - 1]

    input_list = list(map(int, stdin.strip().split()))
    M, N = input_list[0], input_list[1]
    coins = input_list[2:]

    for i in range(M):
        for j in range(N):
            if coins[i * N + j] >= 1:
                return "0"

    visited = bfs(M, N, coins)

    if visited:
        return "1"
    else:
        return "0"

