def solution(stdin: str) -> str:
    def is_appealing(rows):
        for i in range(1, len(rows), 2):
            if rows[i] < rows[i - 1]:
                return False
        for i in range(len(rows) - 1):
            if abs(rows[i] - rows[i + 1]) > 1:
                return False
        return True

    def generate_patterns(s, rows=[]):
        if s == 0:
            yield rows
        else:
            for i in range(1, s // 2 + 1):
                yield from generate_patterns(s - i, rows + [i])

    def compact(rows):
        return f"{rows[0]},{rows[1]}"

    S = int(stdin)
    patterns = []
    for rows in generate_patterns(S):
        if is_appealing(rows):
            patterns.append((rows[0], rows[1], compact(rows)))

    patterns.sort()
    result = [p[-1] for p in patterns]
    return f"{S}:\n" + "\n".join(result)

