    player = 1
    rounds = 0
    while players > 1:
        rounds += 1
        current_syllables = syllables * rounds
        while current_syllables > 0:
            if player == players:
                player = 1
            else:
                player += 1
            current_syllables -= 1
        if player > players - 1:
            player = 1
        else:
            player += 1
        players -= 1
    return str(player)

