    length = len(message)

    # Find the optimal matrix size
    rows = 1
    cols = length
    while rows * cols < length:
        rows += 1
        cols = length // rows
    matrix = [[''] * cols for _ in range(rows)]

    # Fill the matrix in row-major order
    for i in range(rows):
        for j in range(cols):
            if (i * cols + j) < length:
                matrix[i][j] = message[i * cols + j]

    # Read the matrix in column-major order
    result = []
    for j in range(cols):
        for i in range(rows):
            if j < len(matrix[i]):
                result.append(matrix[i][j])

    return ''.join(result)

