def solution(stdin: str) -> str:
    N = int(stdin.split('\n')[0])
    words = stdin.split('\n')[1:]
    number_of_letters = len(''.join(words).replace('$', ''))

    def name_number(n: int) -> str:
        if n == 0:
            return ''
        if n == 10:
            return 'ten'
        if n == 11:
            return 'eleven'
        if n == 12:
            return 'twelve'
        if n == 13:
            return 'thirteen'
        if n == 14:
            return 'fourteen'
        if n == 15:
            return 'fifteen'
        if n == 16:
            return 'sixteen'
        if n == 17:
            return 'seventeen'
        if n == 18:
            return 'eighteen'
        if n == 19:
            return 'nineteen'
        if n < 10:
            return name_number(n - n % 10) + name_number(n % 10)
        if n < 20:
            return name_number(10) + name_number(n % 10)
        if n < 100:
            return name_number(n // 10 * 10) + name_number(n % 10)
        if n < 1000:
            return name_number(n // 100) + 'hundred' + (name_number(n % 100) if n % 100 else '')
        return None

    return ' '.join(words).replace('$', name_number(number_of_letters))

