    sven_symbols = input_data[1]
    num_friends = int(input_data[2])
    friend_symbols = input_data[3:]

    # Define the rules of the game
    rules = {'S': ['P'], 'P': ['R'], 'R': ['S']}

    # Calculate the actual score
    actual_score = 0
    for i in range(rounds):
        sven_symbol = sven_symbols[i]
        for friend in friend_symbols:
            if friend[i] in rules[sven_symbol]:
                actual_score += 2
            elif friend[i] == sven_symbol:
                actual_score += 1

    # Calculate the largest possible score
    largest_possible_score = 0
    for i in range(rounds):
        sven_symbol = sven_symbols[i]
        num_wins = 0
        for friend in friend_symbols:
            if friend[i] in rules[sven_symbol]:
                num_wins += 1
        largest_possible_score += 2 * num_wins
        if num_friends - num_wins > 0:
            largest_possible_score += (num_friends - num_wins)

    return str(actual_score) + '\n' + str(largest_possible_score)

