    last_time = -1

    for t in times:
        if t == last_time:
            continue
        if is_running:
            seconds += t - last_time - 1
        else:
            seconds = t - last_time - 1
        is_running = not is_running
        last_time = t

    if is_running and still_running(seconds, times[n:]):
        return "still running"
    else:
        return seconds

