        # Calculate the distance from the corner of the triangle to the opposite side
        a, b, c = sorted(triangle)
        return (a**2 + b**2 - c**2) / (2 * b)

    def max_distance(triangles):
        # Calculate the maximum distance from the corner of each triangle to its opposite side
        distances = []
        for triangle in triangles:
            distances.append(distance(triangle))
        return max(distances)

    def min_rod_length(triangles):
        # Calculate the minimum required rod length
        max_d = max_distance(triangles)
        return 2 * max_d

