        lines = stdin.splitlines()
        n = int(lines[0])
        for i in range(1, n + 1):
            a, b, c = map(int, lines[i].split())
            triangles.append((a, b, c))
        return triangles

    triangles = process_input(stdin)
    rod_length = min_rod_length(triangles)
    return f"{rod_length:.4f}"

