    # Split the input string into a list of integers and plus symbols
    tokens = [int(x) if x.isdigit() else x for x in stdin.split('+')]

    # Initialize the count of distinct integers to 0
    count = 0

    # Iterate over all possible interpretations of the plus symbols
    for i in range(2 ** (len(tokens) // 2)):
        # Interpret each plus symbol as either addition or string concatenation
        expr = ''
        for j, token in enumerate(tokens):
            if j % 2 == 0:
                expr += str(token)
            elif i & (1 << (j // 2 - 1)):
                expr += '+'+str(token)
            else:
                expr += token

        # Evaluate the expression and count the number of distinct integers
        distinct_integers = set(eval(expr))
        count += len(distinct_integers)

    return count

