    current_fridge = list(map(int, stdin.split()[4:]))

    def simulate(slot_distribution, n, m, s, d):
        total_bottles = sum(slot_distribution)
        if total_bottles < n:
            return 0
        else:
            cold_bottles = sum(slot_distribution[:d])
            if cold_bottles >= m:
                return 1
            else:
                return 0

    def helper(n, m, s, d, current_fridge, index, remaining_bottles, result):
        if remaining_bottles == 0 and simulate(result, n, m, s, d) == 1:
            return result
        if index == s:
            return None
        if remaining_bottles <= 0:
            return None

        current_slot = current_fridge[index]
        if current_slot == 0:
            return None

        new_result = result.copy()
        new_result[index] += 1
        new_fridge = current_fridge.copy()
        new_fridge[index] -= 1

        return helper(n, m, s, d, new_fridge, index + 1, remaining_bottles - 1, new_result) or helper(n, m, s, d, current_fridge, index + 1, remaining_bottles, result)

    result = helper(n, m, s, d, current_fridge, 0, n, [0] * s)

    if result:
        return ' '.join(map(str, result))
    else:
        return "impossible"

