def solution(stdin: str) -> str:
    # Read the input
    lines = stdin.split('\n')
    n, q = map(int, lines[0].split())
    group_sizes = [1] * (n + 1)
    group_parent = list(range(n + 1))

    def find_parent(x: int) -> int:
        if group_parent[x] == x:
            return x
        group_parent[x] = find_parent(group_parent[x])
        return group_parent[x]

    def union_parent(x: int, y: int) -> None:
        x_parent = find_parent(x)
        y_parent = find_parent(y)
        if x_parent == y_parent:
            return
        if group_sizes[x_parent] < group_sizes[y_parent]:
            group_parent[x_parent] = y_parent
            group_sizes[y_parent] += group_sizes[x_parent]
        else:
            group_parent[y_parent] = x_parent
            group_sizes[x_parent] += group_sizes[y_parent]

    # Process the queries
    output = []
    for i in range(1, q + 1):
        query = lines[i].split()
        if query[0] == 't':
            a, b = map(int, query[1:])
            union_parent(a, b)
        else:
            a = int(query[1])
            output.append(group_sizes[find_parent(a)])

    # Return the output
    return '\n'.join(map(str, output))

