    used_drawers = set()
    res = []

    for i in range(N):
        stored = False
        if A[i] not in used_drawers:
            used_drawers.add(A[i])
            res.append("LADICA")
            stored = True

        if not stored and B[i] not in used_drawers:
            used_drawers.add(B[i])
            res.append("LADICA")
