            if abs(arr[i - 1] - arr[i]) <= abs(arr[i] - arr[i + 1]):
                return False
        return True

    def helper(start):
        if start == n - 1:
            return temperatures
        for i in range(start, n):
            if i != start and temperatures[i] == temperatures[start]:
                continue
            for j in range(start, i):
                if j != start and temperatures[j] == temperatures[i]:
                    continue
                temperatures[start], temperatures[j] = temperatures[j], temperatures[start]
                temperatures[i], temperatures[start + 1] = temperatures[start + 1], temperatures[i]
                result = helper(start + 1)
                if result:
                    return result
                temperatures[start], temperatures[j] = temperatures[j], temperatures[start]
                temperatures[i], temperatures[start + 1] = temperatures[start + 1], temperatures[i]

    result = helper(0)

    if not result or not is_valid(result):
        return "impossible"
    return " ".join(map(str, result))

