    N, grid = int(stdin.strip().split('\n')[0]), list(map(list, stdin.strip().split('\n')[1:]))

    def is_valid(x, y):
        return 1 <= x <= N and 1 <= y <= N

    def bfs(grid, start_x, start_y):
        queue = [(start_x, start_y, 0)]
        visited = set()

        while queue:
            x, y, distance = queue.pop(0)
            if (x, y) not in visited:
                visited.add((x, y))

                if x == 1 and y == 1:
                    return distance

                for dx, dy in [(-2, 1), (-2, -1), (2, 1), (2, -1), (-1, 2), (-1, -2), (1, 2), (1, -2)]:
                    new_x, new_y = x + dx, y + dy
                    if is_valid(new_x, new_y) and grid[new_x - 1][new_y - 1] != '#':
                        queue.append((new_x, new_y, distance + 1))

        return -1

    for i in range(N):
        for j in range(N):
            if grid[i][j] == 'K':
                return bfs(grid, i + 1, j + 1)

    return -1

