    for i in range(0, 25):
        row = []
        for j in range(0, 5):
            if i < 25 - 1:
                row.append(key[5 * i + j])
            else:
                row.append('X')
        table.append(row)

    for i in range(len(table)):
        print(' '.join(table[i]))

    def encrypt_pair(a, b):
        a_pos = [(i, j) for i in range(5) for j in range(5) if table[i][j] == a][0]
        b_pos = [(i, j) for i in range(5) for j in range(5) if table[i][j] == b][0]

        if a_pos[0] == b_pos[0]:
            return table[(a_pos[0] + 0) % 5][(a_pos[1] + 1) % 5], table[(b_pos[0] + 0) % 5][(b_pos[1] + 1) % 5]
        elif a_pos[1] == b_pos[1]:
            return table[(a_pos[0] + 1) % 5][(a_pos[1] + 0) % 5], table[(b_pos[0] + 1) % 5][(b_pos[1] + 0) % 5]
        else:
            return table[(a_pos[0] + 0) % 5][(a_pos[1] + 0) % 5], table[(b_pos[0] + 0) % 5][(b_pos[1] + 0) % 5]

    def encrypt(text):
        encrypted_text = ''
        for i in range(0, len(text), 2):
            pair = text[i:i+2]
            if pair[0] == pair[1]:
                encrypted_text += pair[0] + 'X'
            else:
                encrypted_text += ''.join(encrypt_pair(pair[0], pair[1]))
        return encrypted_text

    return encrypt(plaintext)

