        remainder = 0
        for i in range(len(num)):
            if i >= y:
                break
            remainder = (remainder * 10 + num[i]) % (10 ** x)
        return remainder

    def get_min_operations(num: list, x: int, y: int) -> int:
        remainder = get_remainder(num, x, y)
        if remainder == 10 ** y:
            return 0

        for i in range(len(num) - 1, -1, -1):
            if i < y:
                break
            if num[i] == 0:
                num[i] = 1
                remainder = (remainder * 10 + 1) % (10 ** x)
                if remainder == 10 ** y:
                    return len(num) - i - 1
            else:
                num[i] = 0
                remainder = (remainder - 1) % (10 ** x)
                if remainder == 10 ** y:
                    return len(num) - i - 1
        return len(num)

    return str(get_min_operations(number, x, y))

