    def is_valid_square(m, n, tiles):
        if m % 2 == 1 or n % 2 == 1:
            return False

        for i in range(0, m, 2):
            for j in range(0, n, 2):
                if not is_valid_cell(i, j, m, n, tiles):
                    return False
        return True

    def is_valid_cell(i, j, m, n, tiles):
        for tile in tiles:
            if (
                i + 1 < m
                and j + 1 < n
                and tile[0] == tile[1] == tile[2] == tile[3]
                and is_valid_placement(i, j, tile, m, n)
            ):
                return True
        return False

    def is_valid_placement(i, j, tile, m, n):
        if (
            is_inside(i, j, m, n)
            and not is_intersecting(i, j, tile, m, n)
            and is_parallel(i, j, tile, m, n)
        ):
            return True
        return False

    def is_inside(i, j, m, n):
        return 0 <= i < m and 0 <= j < n

    def is_intersecting(i, j, tile, m, n):
        for k in range(i, i + 2):
            for l in range(j, j + 2):
                if tile[0] == m - 1 and l == 0:
                    return True
                if tile[1] == m - 1 and k == 0:
                    return True
                if tile[2] == n - 1 and l == n - 1:
                    return True
                if tile[3] == n - 1 and k == m - 1:
                    return True
        return False

    def is_parallel(i, j, tile, m, n):
        if i == 0 and j == 0:
            return True
        if i == 0 and j != 0:
            return tile[0] == tile[2]
        if i != 0 and j == 0:
            return tile[1] == tile[3]
        return False

