    def is_substring(s1: str, s2: str) -> bool:
        for i in range(len(s2) - len(s1) + 1):
            if s1 == s2[i:i + len(s1)]:
                return True
        return False

    def find_first_position(s: str, strings: list) -> int:
        for i, string in enumerate(strings):
            if is_substring(s, string):
                return i
        return -1

    def find_next_position(s: str, strings: list, start: int) -> int:
        for i, string in enumerate(strings[start + 1:], start + 1):
            if is_substring(s, string):
                return i
        return -1

    def can_reorder(n: int, strings: list) -> bool:
        for i in range(n):
            if find_first_position(strings[i], strings[:i]) == -1:
                if find_next_position(strings[i], strings, i) == -1:
                    return False
        return True

    def reorder_strings(n: int, strings: list) -> list:
        reordered_strings = []
        for i in range(n):
            if find_first_position(strings[i], reordered_strings) == -1:
                if find_next_position(strings[i], strings, i) == -1:
                    reordered_strings.append(strings[i])
        return reordered_strings

    lines = stdin.strip().split('\n')
    n = int(lines[0])
    strings = lines[1:]

    if can_reorder(n, strings):
        return "YES\n" + "\n".join(reorder_strings(n, strings))
    else:
        return "NO"

