            return False
        for t in range(j1 - i1):
            if words[i1 + t] != words[i2 + t]:
                return False
        return True

    def min_length(start: int, end: int, memo: dict) -> int:
        if start > end:
            return 0
        if (start, end) in memo:
            return memo[(start, end)]
        memo[(start, end)] = float('inf')
        for i in range(start, end):
            for j in range(i + 1, end + 1):
