    def sort_matrix(matrix: List[List[int]]) -> Tuple[str, str]:
        rows_inversion = ""
        for i, row in enumerate(matrix):
            if row != sorted(row):
                matrix[i] = [1 - elem for elem in row]
                rows_inversion += "1"
            else:
                rows_inversion += "0"

