            if column != sorted(column):
                for i in range(len(matrix)):
                    matrix[i][j] = 1 - matrix[i][j]
                columns_inversion += "1"
            else:
                columns_inversion += "0"

        return rows_inversion, columns_inversion

    n, m = map(int, stdin.readline().strip().split())
    matrix = [list(map(int, stdin.readline().strip().split())) for _ in range(n)]

    if is_sorted(matrix):
        return "YES\n" + "0" * n + "\n" + "0" * m
    else:
        rows_inversion, columns_inversion = sort_matrix(matrix)
        if is_sorted(matrix):
