    def is_lexicographically_less(x: List[int], y: List[int]) -> bool:
        for i in range(n):
            if x[i] < y[i]:
                return True
            elif x[i] > y[i]:
                return False
        return False

    def find_min_c(a: List[int], b: List[int]) -> List[int]:
        min_c = [0] * n
        for i in range(n):
            min_c[i] = (a[i] + b[i]) % n
        return min_c

    min_b = sorted(b)
    min_c = find_min_c(a, min_b)

    for i in range(n - 1):
        if is_lexicographically_less(min_c, find_min_c(a, min_b[i + 1:] + min_b[:i + 1])):
            continue
        else:
            min_b = min_b[i + 1:] + min_b[:i + 1]
            min_c = find_min_c(a, min_b)

