        visited = [False] * len(graph)
        path = [None] * len(graph)
        cycle = [False] * len(graph)

        def dfs(node, parent):
            visited[node] = True
            path[node] = parent
            for child in graph[node]:
                if not visited[child]:
                    dfs(child, node)
                elif not cycle[child] and child != parent:
                    while node != child:
                        cycle[node] = True
                        node = path[node]

        for node in range(len(graph)):
            if not visited[node]:
                dfs(node, -1)

        return not any(cycle)

    def convert_to_graph(n, edges):
        graph = [[] for _ in range(n)]
        for i, edge in enumerate(edges):
            u, v = edge
            graph[u - 1].append(v - 1)
            graph[v - 1].append(u - 1)
        return graph

