def solution(stdin: str) -> str:
    def find(x, parent):
        if parent[x] == x:
            return x
        return find(parent[x], parent)

    def union(x, y, parent, rank):
        xroot = find(x, parent)
        yroot = find(y, parent)

        if xroot == yroot:
            return False

        if rank[xroot] < rank[yroot]:
            parent[xroot] = yroot
        elif rank[xroot] > rank[yroot]:
            parent[yroot] = xroot
        else:
            parent[yroot] = xroot
            rank[xroot] += 1
        return True

    def kruskal(n, m, edges):
        parent = [i for i in range(n + 1)]
        rank = [0] * (n + 1)
        mst_cost = 0
        mst_edges = []

        for e in sorted(edges, key=lambda x: x[2]):
            if union(e[0], e[1], parent, rank):
                mst_cost += e[2]
                mst_edges.append(e)

        return mst_cost, mst_edges

    def process_graph(n, m, edges):
        mst_cost, mst_edges = kruskal(n, m, edges)
        modified_edges = [e for e in edges if e not in mst_edges]

        for e in modified_edges:
            if union(*e, parent, rank):
                e[2] += 1
            else:
                e[2] -= 1

        new_mst_cost, _ = kruskal(n, m, modified_edges)

        return abs(new_mst_cost - mst_cost)

    lines = stdin.split('\n')
    n, m = map(int, lines[0].split())
    edges = [list(map(int, line.split())) for line in lines[1:m + 1]]

    return str(process_graph(n, m, edges))

