        if target == 0:
            return 0
        if not shovels or target < 0:
            return float('inf')
        if (len(shovels), target) in memo:
            return memo[(len(shovels), target)]

        min_cost = float('inf')
        for i in range(len(shovels)):
            for j in range(i, len(shovels)):
                for k in range(j, len(shovels)):
