    def is_valid(board: List[List[str]], i: int, j: int, h: int, w: int) -> bool:
        if i < 0 or i >= h or j < 0 or j >= w:
            return False
        return board[i][j] == "#"

    def dfs(board: List[List[str]], i: int, j: int, h: int, w: int) -> None:
        if not is_valid(board, i, j, h, w):
            return

        board[i][j] = "#"

        dfs(board, i - 1, j, h, w)
        dfs(board, i + 1, j, h, w)
        dfs(board, i, j - 1, h, w)
        dfs(board, i, j + 1, h, w)

    stdin = stdin.split("\n")
    h, w = map(int, stdin[0].split())
    board = [list(stdin[i]) for i in range(1, h + 1)]

    dfs(board, 0, 0, h, w)

    for row in board:
        if "." in row:
            return "No"
    return "Yes"

