    def find(parent, i):
        if parent[i] == i:
            return i
        return find(parent, parent[i])

    def union(parent, rank, x, y):
        xroot = find(parent, x)
        yroot = find(parent, y)

        if xroot == yroot:
            return False

        if rank[xroot] < rank[yroot]:
            parent[xroot] = yroot
        elif rank[xroot] > rank[yroot]:
            parent[yroot] = xroot
        else:
            parent[yroot] = xroot
            rank[xroot] += 1
        return True

    input_list = list(map(int, stdin.split()))
    N, M = input_list[0], input_list[1]
    edges = input_list[2:]

    parent = [i for i in range(N + 1)]
    rank = [0] * (N + 1)
    bridges = 0

    for i in range(0, M, 2):
        u, v = edges[i], edges[i + 1]
        if union(parent, rank, u, v):
            bridges += 1

    return str(bridges)

