    def dijkstra(start, target):
        dist = [float('inf')] * (n + 1)
        dist[start] = 0
        visited = [False] * (n + 1)
        queue = []
        for i in range(1, n + 1):
            heapq.heappush(queue, (dist[i], i))
        while queue:
