            if u == target:
                return d
            if visited[u]:
                continue
            visited[u] = True
            for x, y, w in roads:
                if x == u:
                    v = y
                elif y == u:
                    v = x
                else:
                    continue
                if not visited[v]:
                    new_dist = d + w
                    if new_dist < dist[v]:
                        dist[v] = new_dist
                        heapq.heappush(queue, (new_dist, v))
        return float('inf')

    def calc_cost(roads, a, b):
        cost = 0
        for x, y, w in roads:
            if x == a and y == b:
                cost += w
            elif x == b and y == a:
                cost += w
        return cost

