    def is_sorted(arr):
        for i in range(len(arr) - 1):
            if arr[i] > arr[i + 1]:
                return False
        return True

    def swap(arr, i, j):
        arr[i], arr[j] = arr[j], arr[i]

    def next_permutation(arr):
        n = len(arr)
        i = n - 2
        while i >= 0 and arr[i] >= arr[i + 1]:
            i -= 1
        if i < 0:
            return False
        j = n - 1
        while arr[j] <= arr[i]:
            j -= 1
        swap(arr, i, j)
        left = i + 1
        right = n - 1
        while left < right:
            swap(arr, left, right)
            left += 1
            right -= 1
        return True

    def find_min_permutation(permutation):
        permutation = sorted(permutation)
        while not is_sorted(permutation):
            next_permutation(permutation)
        return permutation

    q = int(stdin.readline())
    result = []
    for _ in range(q):
        n = int(stdin.readline())
        permutation = list(map(int, stdin.readline().split()))
        min_permutation = find_min_permutation(permutation)
        result.append(" ".join(map(str, min_permutation)))
    return "\n".join(result)

