    def find_subsegments(a: List[int], n: int, k: int) -> Optional[List[int]]:
        if n < k or k < 1:
            return None

        if k == 1:
            if is_odd(sum(a)):
                return [n]
            else:
                return None

        if k == n:
            if all(is_odd(x) for x in a):
                return list(range(1, n + 1))
            else:
                return None

        total_sum = sum(a)
        if total_sum % 2 == 0:
