        if x == M - 1 and y == N - 1:
            return height
        dx = (0, 1)
        dy = (1, 0)
        for i in range(2):
            nx, ny = x + dx[i], y + dy[i]
            if 0 <= nx < M and 0 <= ny < N:
                new_height = max(height, vault[nx][ny])
                if new_height < vault[x][y] + 1:
                    continue
