    def binary_search_c(t):
        left, right = 1, 1000000
        while left + 1 < right:
            mid = (left + right) // 2
            if time_to_compute(mid) + time_to_distribute(mid) <= t:
                right = mid
            else:
                left = mid
        return right

    t = time_to_compute(1) + time_to_distribute(1)
    c = binary_search_c(t)

    return f"{t:.6f} {c:.6f}\n"

