    def dijkstra(graph, start, visited, distances):
        vertices = len(graph)
        visited[start] = True
        distances[start] = 0

        for _ in range(vertices - 1):
            min_dist = float('inf')
            for v in range(vertices):
                if not visited[v] and distances[v] < min_dist:
                    min_dist = distances[v]
                    current_vertex = v
            visited[current_vertex] = True

            for neighbor in range(vertices):
                if not visited[neighbor] and graph[current_vertex][neighbor] != -1:
                    distance = graph[current_vertex][neighbor]
                    if distances[current_vertex] + distance < distances[neighbor]:
                        distances[neighbor] = distances[current_vertex] + distance

    n = int(stdin.readline().strip())
    distances = [list(map(int, stdin.readline().strip().split())) for _ in range(n)]

    graph = [[-1] * n for _ in range(n)]
    for i in range(n):
        for j in range(n):
            if i != j and distances[i][j] != 0:
                graph[i][j] = distances[i][j]

    visited = [False] * n
    for i in range(n):
        dijkstra(graph, i, visited, distances)

    roads = []
    for i in range(n):
        for j in range(n):
            if i < j and distances[i][j] != float('inf'):
                roads.append((i + 1, j + 1))

    return '\n'.join([' '.join(map(str, road)) for road in roads])

