    A, B, K, G = map(int, stdin.readline().split())
    start_intersections = list(map(int, stdin.readline().split()))
    streets = [tuple(map(int, stdin.readline().split())) for _ in range(G)]
    M = int(stdin.readline())
    streets += [tuple(map(int, stdin.readline().split())) for _ in range(M)]

    def find_intersection(street):
        if street[0] in start_intersections:
            return street[0]
        if street[1] in start_intersections:
            return street[1]

    def is_intersection_free(intersection, street):
        if intersection not in [street[0], street[1]]:
            return True
        if intersection == street[0] and street[0] < street[1]:
            return True
        if intersection == street[1] and street[1] < street[0]:
            return True
        return False

    def get_next_street(intersection, streets):
        for street in streets:
            if is_intersection_free(intersection, street):
                yield street

    def get_shortest_path(start_intersection, target_intersection, streets):
        visited = set()
        queue = [(0, start_intersection)]
        while queue:
            time, intersection = queue.pop(0)
            if intersection == target_intersection:
                return time
            if intersection not in visited:
                visited.add(intersection)
                for street in get_next_street(intersection, streets):
                    queue.append((time + street[2], street[0] if intersection == street[1] else street[1]))

    return get_shortest_path(A, B, streets) + K

